con:configuration.scriptText:
import groovy.json.JsonSlurper

def response=messageExchange.response.responseContent;
def json1=new JsonSlurper().parseText(response);
def id=json1.data.id
log.info id
def id1=id.toString();

context.testCase.testSuite.setPropertyValue("_id",id1)

